using System;
using System.Web.Http;
using System.Text;
using WebApi2.Demo.Common;
using WebApi2.Demo.OutputCache;

namespace WebApi2.Demo.Service.Controllers.test
{
    //设置controller缓存
    [CacheOutput(ServerTimeSpan = 100, ClientTimeSpan = 50)]
    public class TestController : BaseApiController
    {
        //test/getname  
        //说明采用route特性路由和默认模版路由只会生效一个  
        //在路由设置参数 那么将不会进入参数验证  
        [Route("b/c/{a}")]
        [JsonpFormatter]
        [IgnoreCacheOutput]//退出全局缓存
        public string GetTime(string a)
        {
            //throw new  ArgumentException("name参数不符合标准");
            "客户端调用了一次getname方法".LogInfo();
            return DateTime.Now.ToString() + "=" + a;
        }


        //test/getname/id
        [Route("a/b/d")]
        [JsonpFormatter]
        public Person GetName()
        {
            return (new Person() { age = 28, name = "88fff", mobile = "13683461619", birthday = DateTime.Now });
        }


        //test/getage
        public string GetAge()
        {
            StringBuilder msg = new StringBuilder();
            for (int i = 0; i < 100; i++)
            {
                msg.Append("");
            }
            return msg.ToString();
        }
        //test/getage?age={age}  
        [HttpPost]
        public string GetAge([FromBody]string age, [FromBody]string name)
        {
            if (int.Parse(age) < 20)
            {
                throw new Exception("age值太小");
            }
            return "我今年" + age + "岁" + "我的名字：" + name;
        }


        //test/people?name={name}&age={age}&mobile={mobile}
        [HttpPost]
        public string People([FromBody]Person person)
        {
            return string.Format("我叫{0},今年{1}岁,手机{2}", person.name, person.age, person.mobile);
        }

        [AcceptVerbs("POST")]
        public string Post([FromBody]string name)
        {
            return string.Format("{0}", name);
        }

        public class Person
        {
            [Verify(type = VerifyType.regexpress,
                pattern = @"^\d[1-9]{1,2}$",
                errorMessage = "age必须是0 - 99的数字")]
            public int age { get; set; }

            [Verify(true)]
            public string name { get; set; }

            [Verify(VerifyType.mobile)]
            public string mobile { get; set; }
            //设置不序列化输出
            //[JsonIgnore]
            public DateTime birthday { get; set; }
        }
    }
}